// auto enabling/disabling bopis_service_down feature flag based on the given datetime in theme settings
var bopis_service_down_start_date = "";
var bopis_service_down_end_date = "";
if (bopis_service_down_start_date && bopis_service_down_end_date) {
  var bopis_service_down_start_date_ISO = new Date(bopis_service_down_start_date).toISOString();
  var bopis_service_down_end_date_ISO = new Date(bopis_service_down_end_date).toISOString();
  var currentDateTime = new Date().toISOString();
  if (currentDateTime >= bopis_service_down_start_date_ISO && currentDateTime <= bopis_service_down_end_date_ISO) {
    window.ENV.Featureflags.bopis_service_down = true;
  } else {
    window.ENV.Featureflags.bopis_service_down = false;
  }
}

