var shopifyDomain = 'staples-canada.myshopify.com'
var getCorrectSegmentKey = function() {
  var isKioskSet = false;
  var isKioskLocation = checkIfKioskLocation();

  if (window.localStorage.getItem('app_mode') === 'kiosk') {
    isKioskSet = true;
  } else if (isKioskLocation) {
    window.localStorage.setItem('app_mode', 'kiosk');
    isKioskSet = true;
  }

  if (isKioskSet) {
    if (shopifyDomain === "staples-canada.myshopify.com") {
      return 'F0gQOFZVEsZ9VgQ8hIfx0SJyAcnLHkCk';
    } else {
      return '7nRW2RkXAB7n5SSr5o9VlTvjxNZSIoyf';
    }
  } else {
    if (shopifyDomain === "staples-canada.myshopify.com") {
      return 'lxKYwUOWD2vPiTxPCltkEoRHEUXWmT8i';
    } else {
      return 'QvIwW3U88XAEMp9YW9Ag6oDJWwGpDE0b';
    }
  }
};
