  // Global Analytics
  var urlParams = new URLSearchParams(window.location.search).toString();
  var breakpoints = {
    'sm': '576',
    'md': '768',
    'lg': '992',
    'xl': '1200',
    'xxl': '1440'
  }
  var viewportSize = '';
  if (window.innerWidth <= breakpoints.sm) {
    viewportSize = 'small screen';
  } else if (window.innerWidth <= breakpoints.md && window.innerWidth > breakpoints.sm) {
    viewportSize = 'medium screen';
  } else if (window.innerWidth <= breakpoints.lg && window.innerWidth > breakpoints.md) {
    viewportSize = 'large screen';
  } else if (window.innerWidth <= breakpoints.xl && window.innerWidth > breakpoints.lg) {
    viewportSize = 'extra large screen';
  } else if (window.innerWidth > breakpoints.xl) {
    viewportSize = 'extra extra large screen';
